            <li>Virtual Worlds</li>
            <li>Trading Cards</li>
            <li>Collectibles</li>
            <li>Sports</li>
            <li>Utility</li>
          </ul>
        </div>
      </div>
      <div className="cardWrapper">
        {list.map((URI, id) => {
