import React, { useEffect, useState } from "react";

import "./NftList.css";
import ERC721abi from "./ERC721abi";

const CA = "0xc5ea1576fda2b80020d30f06d656646fb0981233";

function NftList({ web3, account }) {
  const [list, SetList] = useState("");
  useEffect(async () => {
    console.log(web3);
    console.log(account);
    const nftContract = await new web3.eth.Contract(ERC721abi, CA);
    const total = await nftContract.methods.totalSupply().call();

    let arr = [];
    for (let i = 1; i <= total; i++) {
      arr.push(i);
    }
    for (let Id of arr) {
      let host = await nftContract.methods.ownerOf(Id).call();
      console.log(host);
      if (String(host).toLowerCase() === account) {
        let tokenURI = await nftContract.methods.tokenURI(Id).call();
        arr.push(tokenURI);
      }
    }

    // SetList(arr)
  }, []);
  // const useEffect = async () => {
  //   /*컨트랙트에 연결해서 /
  //   const nftContract = await new web3.eth.Contract(ERC721abi, CA);
  //   const total = await nftContract.methods.totalSupply().call();
  //   console.log(total);

  // /페이지 열 때 리스트 만들도록 했는데 배열로 못받아오네요/
  // useEffect(() => {
  //   /nftContract 선언되어있는데 nftContract is not defined에러도 뜨구요 */
  //   makeList();
  //   console.log(list);
  //   return () => {
  //   };
  // }, []);

  return (
    <header className="nftListPage">
      <div clasName="nftExplorer">
        <text className="nftListText">Explore Collections</text>
        <div>
          <ul className="nftList">
            <li>All NFTs</li>
            <li>Art</li>
            <li>Music</li>
            <li>Domain Names</li>
            <li>Virtual Worlds</li>
            <li>Trading Cards</li>
            <li>Collectibles</li>
            <li>Sports</li>
            <li>Utility</li>
          </ul>
        </div>
      </div>
      <div className="cardWrapper">
        {/* {list.map((URI, id) => {
          <span className="card">
            <div className="cardImgDiv">
              <img className="cardImg" src={URI} alt="test" />
            </div>
