import React, { useState } from "react";
import "./MakeNFT.css";
function MakeNFT() {
  const [img, SetImg] = useState("");
  const [name, SetName] = useState("");
  const [link, SetLink] = useState("");
  const [description, SetDescription] = useState("");

  const clickButton = () => {
    console.log(img, name, link, description);
  };

  const imgChange = async (e) => {
    if (e.files === undefined) {
      let reader = new FileReader();
      console.log("sdsdsd");

      reader.readAsDataURL(e.target.files[0]);

      reader.onload = (event) => {
        const previewImage = document.querySelector(".img_span");
        console.log(previewImage);
        SetImg(event.target.result);
      };
    }
  };

  const nameChange = (e) => {
    SetName(e.target.value);
  };

  const linkChange = (e) => {
    SetLink(e.target.value);
  };
  const descriptionChange = (e) => {
    SetDescription(e.target.value);
  };
  return (
    <div className="MakeNFT_back">
      <div className="MakeNFT_BOX">
        <div id="title">Create New Item</div>
        <div className="img_box">
          <span className="img_span">img</span>
          <span className="img_example">
            File types supported: JPG, PNG, GIF, SVG, MP4, WEBM, MP3, WAV, OGG,
            GLB, GLTF. Max size: 100 MB
          </span>
          {/* <img src="./img/lamp.png" className="img" /> */}
          <input id="img" type="file" placeholder="img" required />
          <button className="img_button" onClick={imgChange}>
            Select Your Image!
          </button>
        </div>
        <div className="name_box">
          <span className="name_span">name</span>
          <input
            type="text"
            placeholder="name"
            required
            onChange={nameChange}
            value={name}
          />
        </div>
        <div className="link_box">
          <span className="link_span">link</span>
          <input
            type="text"
            placeholder="link"
            required
            onChange={linkChange}
            value={link}
          />
        </div>
        <div className="description_box">
          <span className="description_span">description</span>
          <input
            type="text"
            placeholder="description"
            required
            onChange={descriptionChange}
            value={description}
          />
        </div>
        <button onClick={clickButton}>Create</button>
      </div>
    </div>
  );
}

export default MakeNFT;
