import React, { useState } from "react";
import "./MakeNFT.css";
function MakeNFT({ web3 }) {
  const [img, SetImg] = useState("");
  const [name, SetName] = useState("");
  const [link, SetLink] = useState("");
  const [description, SetDescription] = useState("");
  const [obj, SetObj] = useState({});

  const clickButton = () => {
    if (name !== "" && img !== "" && link !== "" && description !== "") {
      SetObj({
        name: name,
        address: link,
        datae: description,
        img: img,
      });
      makeNFT(obj);
    } else {
      alert("모두 입력해 주세요");
    }
  };

  const makeNFT = (obj) => {};

  const imgChange = async (e) => {
    if (e.files === undefined) {
      let reader = new FileReader();

      reader.readAsDataURL(e.target.files[0]);
      // base64로 인코딩

      reader.onload = (event) => {
        const previewImage = document.querySelector(".img");
        previewImage.src = event.target.result;
        SetImg(event.target.result);
      };
    }
  };

  const nameChange = (e) => {
    SetName(e.target.value);
  };

  const linkChange = (e) => {
    SetLink(e.target.value);
  };
  const descriptionChange = (e) => {
    SetDescription(e.target.value);
  };
  return (
    <div className="MakeNFT_back">
      <div className="MakeNFT_BOX">
        <div id="title">Create New Item</div>
        <div className="img_box">
          <span className="img_span">img</span>
          <span className="img_example">
            File types supported: JPG, PNG, GIF, SVG, MP4, WEBM, MP3, WAV, OGG,
            GLB, GLTF. Max size: 100 MB
          </span>
          <img src="/" className="img" />
          <input
            id="img"
            type="file"
            placeholder="img"
            required
            onChange={imgChange}
          />
          {/* <button id="imgbutton" onClick={imgChange}>
            Select Your Image!
          </button> */}
        </div>
        <div className="name_box">
          <span className="name_span">name</span>
          <input
            type="text"
            placeholder="name"
            required
            onChange={nameChange}
            value={name}
          />
        </div>
        <div className="link_box">
          <span className="link_span">link</span>
          <input
            type="text"
            placeholder="link"
            required
            onChange={linkChange}
            value={link}
          />
        </div>
        <div className="description_box">
          <span className="description_span">description</span>
          <input
            type="text"
            placeholder="description"
            required
            onChange={descriptionChange}
            value={description}
          />
        </div>
        <button className="createbutton" onClick={clickButton}>
          Create
        </button>
      </div>
    </div>
  );
}

export default MakeNFT;
